mapper.select:
select m.member_name, p.id, p.post_title, p.post_content, p.post_read_count, p.member_id, p.created_date, p.updated_date
from tbl_member m join tbl_post p
on m.id = p.member_id and p.id = #{id}

mapper.update:
update tbl_post
set post_content = #{postContent}
where member_id = #{memberId}
